DECLARE
  cursor table_ids is select owner,table_name,registration_id from sde.table_registry 
  where table_name in (
     select table_name 
	 from sde.column_registry 
	 where column_name='CIRCUITCOLOR'
	 and table_name like 'PRI%');
sql_stmt varchar2(2000);
result_count number;
BEGIN
    for tabname in table_ids loop
      sql_stmt := 'update '||tabname.owner||'.'||tabname.table_name||' cs set cs.circuitcolor=(select cct.circuitcolor from edgis.circuitsource cct where cct.circuitid=cs.circuitid group by cct.circuitcolor ) where exists (select 1 from edgis.circuitsource cct1 where  cct1.circuitid=cs.circuitid )';
      dbms_output.put_line(sql_stmt);
      execute immediate sql_stmt;
                  sql_stmt :='select count(*) from '||tabname.owner||'.'||tabname.table_name||' where circuitcolor is null';
                  dbms_output.put_line(sql_stmt);
                  result_count :=0;
      execute immediate sql_stmt into result_count;
                  dbms_output.put_line(result_count);
      sql_stmt := 'update '||tabname.owner||'.A'||tabname.registration_id||' cs set cs.circuitcolor=(select cct.circuitcolor from edgis.circuitsource cct where cct.circuitid=cs.circuitid group by cct.circuitcolor ) where exists (select 1 from edgis.circuitsource cct1 where  cct1.circuitid=cs.circuitid )'; 
      dbms_output.put_line(sql_stmt);
                  execute immediate sql_stmt;
                  sql_stmt :='select count(*) from '||tabname.owner||'.A'||tabname.registration_id||' where circuitcolor is null';
                  dbms_output.put_line(sql_stmt);
                  result_count :=0;
      execute immediate sql_stmt into result_count;
                  dbms_output.put_line(result_count);
                end loop;
                commit;
end;
